import {useState} from 'react';

var contactRegex = /^\d{10}$/;

var emailRegex = /^([a-z\d\.-]+)@([a-z]+)\.([a-z]{2,5})(\.[a-z]{2,8})?$/;

var dobREGEX = /^\d{4}-(0[1-9]|1[012])-(0[1-9]|[12][0-9]|3[01])$/;

var passwordRegex = /^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[!@#$%^&*~?<>])[a-zA-Z0-9!@#$%^&*~?<>]{8,30}$/;

var checkValidation = false;

export default function Form() {

    //States for signup
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [dob, setDob] =useState('');
    const [contact, setContact] = useState('');
    //const [submit, setSubmit] = useState('');

    const handleEmail = (e) => {
        setEmail(e.target.value);
    }

    const handleName = (e) => {
        setName(e.target.value);
    }

    const handlePassword = (e) => {
        setPassword(e.target.value);
    }

    const handleDob = (e) => {
        setDob(e.target.value);
    }

    const handleContact = (e) => {
        setContact(e.target.value);
    }

    function emailValidation(){
        if(!emailRegex.test(email)){
            alert('Error email');
            checkValidation = false;
        } else {
            checkValidation = true;
        }
    }

    function passwordValidation(){
        if(!passwordRegex.test(password)){
            alert('Error password');
            checkValidation = false;
        } else {
            checkValidation = true;
        }
    }

    function contactValidation(){
        if(!contactRegex.test(contact)){
            alert('Error contact');
            checkValidation = false;
        } else {
            checkValidation = true;
        }
    }

    function dobValidation(){
        if(!dobREGEX.test(dob)){
            alert('Error DOB');
            checkValidation = false;
        } else {
            checkValidation = true;
        }
    }

    function printconsole(){
        if(checkValidation) {
            console.log(name+' '+dob+" "+password+" "+contact+" "+email);
        }
    }

    function getDate(){
        var today = new Date();
        document.getElementById("dob").value = today.getFullYear() + '-' + ('0' + (today.getMonth() + 1)).slice(-2) + '-' + ('0' + today.getDate()).slice(-2);
    }

    return (
        <div className='form'>
            <div>
                <h1>
                    User SignUp
                </h1>
            </div>
            <form>

                <label className='label'>
                    Email
                </label>
                <input onChange={handleEmail} className='input' value={email} type='email' onBlur={emailValidation}/>

                <label className='label'>
                    Name
                </label>
                <input onChange={handleName} className='input' value={name} type='text'/>

                <label className='label'>
                    Password
                </label>
                <input onChange={handlePassword} className='input' value={password} type='password' onBlur={passwordValidation}/>

                <label className='label'>
                    Contact
                </label>
                <input onChange={handleContact} className='input' value={contact} type='text' onBlur={contactValidation}/>

                <label className='label'>
                    DOB
                </label>
                <input onChange={handleDob} className='input' value={dob} type='date' id='dob' onBlur={dobValidation} onLoad={getDate}/>
            </form>
            <button type='submit' onClick={printconsole} className='submitButton'>Submit</button>
        </div>
    )

}
