import {useState} from 'react';
import axios from 'axios';

var contactRegex = /^\d{10}$/;

var emailRegex = /^([a-z\d\.-]+)@([a-z]+)\.([a-z]{2,5})(\.[a-z]{2,8})?$/;

var dobREGEX = /^\d{4}-(0[1-9]|1[012])-(0[1-9]|[12][0-9]|3[01])$/;

var pwdRegex = /^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[!@#$%^&*~?<>])[a-zA-Z0-9!@#$%^&*~?<>]{8,30}$/;

var checkValidation = false;

export default function Form() {

    //States for signup
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [pwd, setpwd] = useState('');
    const [dob, setDob] =useState('');
    const [contact, setContact] = useState('');
    const [lang, setLang] = useState('');
    //const [submit, setSubmit] = useState('');

    const handleEmail = (e) => {
        setEmail(e.target.value);
    }

    const handleName = (e) => {
        setName(e.target.value);
    }

    const handlepwd = (e) => {
        setpwd(e.target.value);
    }

    const handleDob = (e) => {
        setDob(e.target.value);
    }

    const handleContact = (e) => {
        setContact(e.target.value);
    }

    const handleLang = (e) => {
        setLang(e.target.value);
    }

    function emailValidation(){
        if(!emailRegex.test(email)){
            alert('Error email');
            checkValidation = false;
        } else {
            checkValidation = true;
        }
    }

    function pwdValidation(){
        if(!pwdRegex.test(pwd)){
            alert('Error pwd');
            checkValidation = false;
        } else {
            checkValidation = true;
        }
    }

    function contactValidation(){
        if(!contactRegex.test(contact)){
            alert('Error contact');
            checkValidation = false;
        } else {
            checkValidation = true;
        }
    }

    function langValidation(){
        if(!lang){
            alert('Lang cannot be empty');
            checkValidation = false;
        }
    }

    function dobValidation(){
        if(!dobREGEX.test(dob)){
            alert('Error DOB');
            checkValidation = false;
        } else {
            checkValidation = true;
        }
    }

    function printconsole(){
        if(!dob){
            alert('Error DOB');
            checkValidation = false;
        }
        if(checkValidation) {
            console.log(name+' '+dob+" "+pwd+" "+contact+" "+email);
        }
    }


    const signUpUser = () => {
        const userObject = {
            name: name,
            email: email,
            pwd: pwd,
            contact: contact,
            dob: dob
        };

        axios.post('http://localhost:9898/signup', userObject)
        .then((res) => {
            console.log(res.data)
        }).catch((error) => {
            console.log(error)
        });
    }

    return (
        <div className='form'>
            <div>
                <h1>
                    User SignUp
                </h1>
            </div>
            <form>

                <label className='label'>
                    Email
                </label>
                <input onChange={handleEmail} className='input' value={email} type='email' onBlur={emailValidation}/>

                <label className='label'>
                    Name
                </label>
                <input onChange={handleName} className='input' value={name} type='text'/>

                <label className='label'>
                    pwd
                </label>
                <input onChange={handlepwd} className='input' value={pwd} type='password' onBlur={pwdValidation}/>

                <label className='label'>
                    Contact
                </label>
                <input onChange={handleContact} className='input' value={contact} type='text' onBlur={contactValidation}/>

                <label className='label'>
                    DOB
                </label>
                <input onChange={handleDob} className='input' value={dob} type='date' id={dob} onBlur={dobValidation} />

                <label className='label'>
                    Lang
                </label>
                <input onChange={handleLang} className='input' value={lang} type='text' onBlur={langValidation}/>
            </form>
            <button type='submit' onClick={signUpUser} className='submitButton'>Submit</button>
        </div>
    )

}
