import {useState} from 'react';

var contactRegex = /^\d{10}$/;

var emailRegex = /^([a-z\d\.-]+)@([a-z]+)\.([a-z]{2,5})(\.[a-z]{2,8})?$/;

var passwordRegex = /^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*[!@#$%^&*~?<>])[a-zA-Z0-9!@#$%^&*~?<>]{8,20}$/;

export default function Form() {

    //States for signup
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [dob, setDob] =useState('');
    const [contact, setContact] = useState('');
    //const [submit, setSubmit] = useState('');

    const handleEmail = (e) => {
        setEmail(e.target.value);
    }

    const handleName = (e) => {
        setUserName(e.target.value);
    }

    const handlePassword = (e) => {
        setPassword(e.target.value);
    }

    const handleDob = (e) => {
        setDob(e.target.value);
    }

    const handleContact = (e) => {
        setContact(e.target.value);
    }

    function emailValidation(){
        if(!emailRegex.test(email)){
            alert('Error email');
        }
    }

    function passwordValidation(){
        if(!passwordRegex.test(password)){
            alert('Error password')
        }
    }

    function contactValidation(){
        if(!contactRegex.test(contact)){
            alert('Error contact');
        }
    }

    return (
        <div className='form'>
            <div>
                <h1>
                    User SignUp
                </h1>
            </div>
            <form>

                <label className='label'>
                    Email
                </label>
                <input onChange={handleEmail} className='input' value={email} type='email' onBlur={emailValidation}/>

                <label className='label'>
                    Name
                </label>
                <input onChange={handleName} className='input' value={name} type='text'/>

                <label className='label'>
                    Password
                </label>
                <input onChange={handlePassword} className='input' value={password} type='password' onBlur={passwordValidation}/>

                <label className='label'>
                    Contact
                </label>
                <input onChange={handleContact} className='input' value={contact} type='text'/>

                <label className='label'>
                    DOB
                </label>
                <input onChange={handleDob} className='input' value={dob} type='date'/>
            </form>
            <button type='submit'  className='submitButton'>Submit</button>
        </div>
    )

}
